mapper.select:
SELECT ci.id, ci.cart_id, ci.quantity 
FROM cart_item AS ci 
WHERE ci.id = #{itemId}

SELECT ci.id, ci.cart_id, ci.quantity 
FROM cart_item AS ci 
WHERE ci.product_id = #{productId} AND ci.cart_id = #{cartId}

mapper.update:
UPDATE cart_item
SET quantity = #{quantity}
WHERE id = #{id}
